import React from 'react'

class Form extends React.Component {
	state = {
		features: {
		  Processor: [
			{
			  name: '17th Generation Intel Core HB (7 Core with donut spare)',
			  cost: 700
			},
			{
			  name: 'Professor X AMD Fire Breather with sidewinder technology',
			  cost: 1200
			}
		  ],
		  "Operating System": [
			{
			  name: 'Ubuntu Linux 16.04',
			  cost: 200
			},
			{
			  name: 'Bodhi Linux',
			  cost: 300
			}
		  ],
		  "Video Card": [
			{
			  name: 'Toyota Corolla 1.5v',
			  cost: 1150.98
			},
			{
			  name: 'Mind mild breeze 2000',
			  cost: 1345
			}
		  ],
		  Display: [
			{
			  name: '15.6" UHD (3840 x 2160) 60Hz Bright Lights and Knobs',
			  cost: 1500
			},
			{
			  name: '15.3" HGTV (3840 x 2160) Home makeover edition',
			  cost: 1400
			},
		  ]
		},
		selected: {
        Processor: {
            name: '17th Generation Intel Core HB (7 Core with donut spare)',
            cost: 700
          },
        "Operating System": {
            name: 'Ubuntu Linux 16.04',
            cost: 200
          },
        "Video Card":{
            name: 'Toyota Corolla 1.5v',
            cost: 1150.98
          },
        Display: {
            name: '15.6" UHD (3840 x 2160) 60Hz Bright Lights and Knobs',
            cost: 1500
          }
      },
	}

	render() {
		const features = Object.keys(this.state.features)
          .map(key => {
            const options = this.state.features[key].map((item, index) => {
				return {
					selectedClass: item.name === this.state.selected[key].name ? 'feature__selected' : '',
					featureClass: 'feature__option ' + features.selectedClass,
					key: index,
					item: item,
					price: new Intl.NumberFormat('en-US', { style: 'currency', currency: 'USD'})
                      .format(item.cost)
				}
			})
			})

		console.log(features)



		return (
			<section className="main__form">
				<h3>TECH SPECS AND CUSTOMIZATIONS</h3>

			</section>
		)		

	}

}

export default Form
