import React from 'react'

class Form extends React.Component {
	componentDidMount(){
		console.log(this.props.stateCopy)
	}

	render() {
		const features = Object.keys(this.props.stateCopy.features)
          .map(key => {
            const options = this.state.features[key].map((item, index) => {
				return {
					selectedClass: item.name === this.state.selected[key].name ? 'feature__selected' : '',
					featureClass: 'feature__option ' + features.selectedClass,
					key: index,
					item: item,
					price: new Intl.NumberFormat('en-US', { style: 'currency', currency: 'USD'})
                      .format(item.cost)
				}
			})
			})

		console.log(features)



		return (
			<section className="main__form">
				<h3>TECH SPECS AND CUSTOMIZATIONS</h3>

			</section>
		)		

	}

}

export default Form
