import React from 'react'

class Form extends React.Component {
	componentDidMount(){
		console.log(this.props.stateCopy)
	}

	updateFeature(feature, newValue) {
		const selected = Object.assign({}, this.state.selected);
		selected[feature] = newValue;
		this.setState({
		  selected
		});
	 }
	render() {
		const features = Object.keys(this.props.stateCopy.features)
          .map(key => {

            const options = this.props.stateCopy.features[key].map((item, index) => {
              const selectedClass = item.name === this.props.stateCopy.selected[key].name ? 'feature__selected' : '';
              const featureClass = 'feature__option ' + selectedClass;

			return <li key={index} className="feature__item">
                <div className={featureClass}
                  onClick={e => this.props.updateFeature(key, item)}>
                    { item.name }
                    ({ new Intl.NumberFormat('en-US', { style: 'currency', currency: 'USD'})
                      .format(item.cost) })
                </div>
              </li>
            });

            return <div className="feature" key={key}>
              <div className="feature__name">{key}</div>
              <ul className="feature__list">
                { options }
              </ul>
            </div>
          });     

		return (
			<section className="main__form">
				<h3>TECH SPECS AND CUSTOMIZATIONS</h3>
				 {features}

			</section>
		)		

	}

}

export default Form
